$red:#cc112a;
$white: #fff;
$gray3: #333;
// COMMON
body, div, p, h1, h2, h3, h4, h5, h6, ul, ol, li, dl, dt, dd, table, th, td, form, fieldset, legend, input, textarea, a, button, select, ul, li, span, em {
  margin: 0;
  padding: 0;
  line-height: 1.1;
  box-sizing: border-box;
}
body{
  color: $gray3;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  font-family: 'Nanum Godic', sans-serif;
}
.inner {
  width: 1100px;
  margin: 0 auto;
  position: relative;
}
img {
  display: block;
}
a {
  text-decoration: none;
}
.btn {
  width: 130px;
  padding: 10px;
  border: 2px solid $gray3;
  border-radius: 4px;
  color: $gray3;
  font-size: 16px;
  font-weight: 700;
  text-align: center;
  box-sizing: border-box;
  display: block;
  transition: .4s;
  &:hover {
    background-color: $gray3;
    color: #fff;
  }
  &.btn--reverse {
    background-color: $gray3;
    color: #fff;
  }
  &.btn--reverse:hover {
    background-color: transparent;
    color: $gray3;
  }

}
.back-to-position.to-right {
  transform: translateX(-150px);
}
.back-to-position.to-left {
  transform: translateX(150px);
}
.show .back-to-position {
  opacity: 1;
  transform: translateX(0);
}
.show .back-to-position.delay-0 {
  transition-delay: 0s;
}
.show .back-to-position.delay-1 {
  transition-delay: .3s;
}
.show .back-to-position.delay-2 {
  transition-delay: .6s;
}
.show .back-to-position.delay-3 {
  transition-delay: .9s;
}

// HEADER
header {
  width: 100%;
  background-color: rgb(252, 252, 252);
  > .inner {
    height: 130px;
      a.logo {
      height: 75px;
      position: absolute;
      top: 0;
      bottom: 0;
      margin: auto;
      img {
        width: 75px;
      }
    }
  }
  .sub-menu {
    position: absolute;
    top: 10px;
    right: 0;
    display: flex;
    ul.menu {
      display: flex;
      li{
        position: relative;
        &::before {
          content: "";
          width: 1px;
          height: 12px;
          background-color: #e5e5e5;
          /* 배치 */
          position: absolute;
          top: 0;
          bottom: 0;
          margin: auto; 
        }
        &:first-child::before {
          display: none;
        }
        a {
          font-size: 12px;
          padding: 11px 16px;
          display: block;
          color: #656565;
          &:hover {
            color: #000;
          }
        }
      }
    }
    .search {
      height: 34px;
      position: relative;
      input {
        width: 36px;
        height: 34px;
        padding: 4px 10px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 5px;
        outline: none;
        background-color: $white;
        transition: width .4s;
        &:focus {
          width: 190px;
          border-color: $red;
        }
      }
      .material-icons {
        height: 24px;
        position: absolute;
        top: 0;
        right: 5px;
        margin: auto;
        transition: .4s;
      }
    }
    .search.focused .material-icons {
      opacity: 0;
    }
  }// .sub-menu

  .main-menu {
    position: absolute;
    bottom: 0;
    right: 120px;
    display: flex;
    .item {
      .item__name {
        padding: 10px 20px 34px 20px;
        font-family: Arial, sans-serif;
        font-size: 13px;
      }
      .item__contents {
        width: 100%;
        position: fixed;
        left: 0;
        display: none;
        z-index: 2;
        .contents__menu {
          background-color: $gray3;
          & > ul {
            display: flex;
            padding: 20px 0 60px;
            & >li {
              width: 220px;
              h4 {
                padding: 3px 0 12px 0;
                font-size: 14px;
                color: $white;
              }
              ul li {
                padding: 5px 0;
                font-size: 12px;
                color: #999;
                cursor: pointer;
                &:hover {
                  color: $red;
                }
              }
            }
          }
        }
      }
      &:hover {
        .item__name {
          background-color: #2c2a29;
          color: $red;
          border-radius: 6px 6px 0 0;
        }
        .item__contents {
          display: block;
        }
      }
            

    }
  }
  .sns {
    position: absolute;
    bottom: 0;
    right: 0;
    display: flex;
    li{
      display: block;
      width: 20px;
      height: 20px;
      margin: 0 2px;
