    }
    .instagram {
      background-image: url("../images/ic_sns_insta.png");
    }
    .youtube {
      background-image: url("../images/ic_sns_youtube.png");
    }
    .happypoint {
      background-image: url("../images/ic_sns_happy.png");
    }
  }

  .badges{
