$red:#cc112a;
$white: #fff;
$gray3: #333;
.back-to-position.to-right {
  transform: translateX(-150px);
}
.back-to-position.to-left {
  transform: translateX(150px);
}
.show .back-to-position {
  opacity: 1;
  transform: translateX(0);
}
.back-to-position {
  opacity: 0;
  transition: 1s;
}
.show .back-to-position.delay-0 {
  transition-delay: 0s;
}
.show .back-to-position.delay-1 {
  transition-delay: .3s;
}
.show .back-to-position.delay-2 {
  transition-delay: .6s;
}
.show .back-to-position.delay-3 {
  transition-delay: .9s;
}

/* YOUTUBE */
.youtube {
  position: relative;
  height: 700px;
  background-color: $gray3;
  overflow: hidden;
}
.youtube .youtube__area {
  width: 1920px;
  position: absolute;
  left: 50%;
  margin-left: calc(1920px / -2);
  top: 50%;
  margin-top: calc(1920px * 9 / 16 / -2);
}
.youtube .youtube__area::before {
  content: "";
  display: block;
  width: 100%;
  height: 0;
  padding-top: 56.25%;
}
.youtube .youtube__cover {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
#player {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
}

.promotion {
  background-color: #f6f5ef;
  position: relative;
  transition: height .4s;
  overflow: hidden;
  .inner{
    display: flex;
    .inner__left {
      width: 65%;
      height: 100%;
      margin: 0 5px;
      .swiper-container {
        width: 100%;
        position: relative;
        // left: 50%;
        // margin-left: calc((700px) / -2);
        .swiper-slide {
          opacity : .5;
          transition: 1s;
          img{ width: 100%;}
        }
        .swiper-slide-active {
          opacity: 1;
        }
        .swiper-pagination {
          // bottom: 20px;
          width: 100%;
          .swiper-pagination-bullet {
            background-color: transparent;
            width: 12px;
            height: 12px;
            border-color: $gray3;
            border-radius: 50%;
            &:last-child {
              margin-right: 0;
            }
          }
          .swiper-pagination-bullet-active {
            border-color: red;
            border-radius: 50%;
          }
      
        }
        .swiper-prev, .swiper-next {
          width: 42px;
          height: 42px;
          border: 2px solid $white;
          background-color: $red;
          border-radius: 50%;
          position: absolute;
          bottom: 10px;
          z-index: 1;
          cursor: pointer;
          outline: none;
          display: flex;
          justify-content: center;
          align-items: center;
          color: $white;
        }
        .swiper-prev {
          right: 65px;
        }
        .swiper-next {
          right: 10px;
        }
      }
    }
    .inner__right {
      width: 35%;
      // height: 100%;
      margin: 0 5px;
      .insta-container {
        height: 100%;
        .insta-wrapper {
          display: flex;
          flex-direction: column;
          height: 100%;
          .insta-slide {
            flex-grow: 1;
            overflow: hidden;
            a {
              .insta-pick1::before, .insta-pick2::before {
                content: "";
                position: absolute;
                top: 5px;
                left: 5px;
                width: 30px;
                height: 30px;
                background-image: url("../images/ic_talk_insta.png");
                background-size:cover;
              }
              .insta-pick1 {
                position: relative;
                width: 100%;
                height: 100%;
                background-image: url("../images/210210014_191956322861718_1523709133934298159_n.jpg");
                background-repeat: no-repeat;
                background-size: cover;
                background-position: top;
              }
              .insta-pick2 {
                position: relative;
                width: 100%;
                height: 100%;
                background-image: url("../images/209256278_347174236769323_7852020195734116621_n.jpg");
                background-repeat: no-repeat;
                background-size: cover;
                background-position: top;
              }
            }
          }
        }
      }
    }
  }
}

// season product
.season-product {
  width: 100%;
  height: 400px;
  background-color: #e2ebf2;
  // position: relative;
  .inner {
    height: 100%;
    p {
      position:absolute;
      font-family: 'Jomhuria', cursive;
      font-size: 100px;
      line-height: 60px;
      left: 30px;
      top: 20%;
    }
    img {
      position: absolute;
      width: 100%;
    }
    .product1{
      width: 33%;
      left: 35%;
      bottom: 10%;
      z-index: 2;
    }
    .product2{
      width: 33%;
      right: 20%;
      bottom: -13%;
      z-index: 4;
    }
    .product3{
      width: 27%;
      right: 10%;
      bottom: 10%;
      z-index: 3;
    }
    .product4{
      width: 28%;
      right: 10%;
      bottom: 30%;
      z-index: 1;
    }
    .product5 {
      width: 28%;
      right: 1%;
      bottom: 33%;
      z-index: 2;
    }
    .product6 {
      width: 25%;
      right: -6%;
      bottom: 37%;
      z-index: 1;
    }
  }
}



#to-top {
  width: 42px;
  height: 42px;
  background-color: #333;
  color: #fff;
  border: 2px solid #fff;
  border-radius: 10px;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  bottom: 100px;
  right: 30px;
  z-index: 9;
}

