}

// mobile
@media (max-width: 767px) {
  .youtube {
    height: 400px !important;
    .youtube__area {
      width: 100% !important;
      height: 100% !important;
      left: 0 !important;
      margin-left: 0  !important;
      top: 0 !important;
      margin-top: 0 !important;
    }
  }
  .promotion {
    .inner {
      width: 100%;
      flex-direction: column;
      .inner__left {
        width: 100% !important;
      }
      .inner__right {
        display: none;
      }
    }
  }
}
