$red:#cc112a;
$white: #fff;
$gray3: #333;

// COMMON
body, div, p, h1, h2, h3, h4, h5, h6, ul, ol, li, dl, dt, dd, table, th, td, form, fieldset, legend, input, textarea, a, button, select, ul, li, span, em {
  margin: 0;
  padding: 0;
  line-height: 1.1;
  box-sizing: border-box;
}
body{
  color: $gray3;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  font-family: 'Nanum Godic', sans-serif;
}
.inner {
  width: 1100px;
  margin: 0 auto;
  position: relative;
}
img {
  display: block;
}
a {
  text-decoration: none;
}
.btn {
  width: 130px;
  padding: 10px;
  border: 2px solid $gray3;
  border-radius: 4px;
  color: $gray3;
  font-size: 16px;
  font-weight: 700;
  text-align: center;
  box-sizing: border-box;
  display: block;
  transition: .4s;
  &:hover {
    background-color: $gray3;
    color: #fff;
  }
  &.btn--reverse {
    background-color: $gray3;
    color: #fff;
  }
  &.btn--reverse:hover {
    background-color: transparent;
    color: $gray3;
  }

}
.back-to-position.to-right {
  transform: translateX(-150px);
}
.back-to-position.to-left {
  transform: translateX(150px);
}
.show .back-to-position {
  opacity: 1;
  transform: translateX(0);
}
.show .back-to-position.delay-0 {
  transition-delay: 0s;
}
.show .back-to-position.delay-1 {
  transition-delay: .3s;
}
.show .back-to-position.delay-2 {
  transition-delay: .6s;
}
.show .back-to-position.delay-3 {
  transition-delay: .9s;
}

// HEADER
header {
  width: 100%;
  background-color: rgb(252, 252, 252);
  > .inner {
    height: 130px;
      a.logo {
      height: 75px;
      position: absolute;
      top: 0;
      bottom: 0;
      margin: auto;
      img {
        width: 75px;
      }
    }
  }
  .sub-menu {
    position: absolute;
    top: 10px;
    right: 0;
    display: flex;
    ul.menu {
      display: flex;
      li{
        position: relative;
        &::before {
          content: "";
          width: 1px;
          height: 12px;
          background-color: #e5e5e5;
          /* 배치 */
          position: absolute;
          top: 0;
          bottom: 0;
          margin: auto; 
        }
        &:first-child::before {
          display: none;
        }
        a {
          font-size: 12px;
          padding: 11px 16px;
          display: block;
          color: #656565;
          &:hover {
            color: #000;
          }
        }
      }
    }
    .search {
      height: 34px;
      position: relative;
      input {
        width: 36px;
        height: 34px;
        padding: 4px 10px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 5px;
        outline: none;
        background-color: $white;
        transition: width .4s;
        &:focus {
          width: 190px;
          border-color: $red;
        }
      }
      .material-icons {
        height: 24px;
        position: absolute;
        top: 0;
        right: 5px;
        margin: auto;
        transition: .4s;
      }
    }
    .search.focused .material-icons {
      opacity: 0;
    }
  }// .sub-menu

  .main-menu {
    position: absolute;
    bottom: 0;
    right: 120px;
    display: flex;
    .item {
      .item__name {
        padding: 10px 20px 34px 20px;
        font-family: Arial, sans-serif;
        font-size: 13px;
      }
      .item__contents {
        width: 100%;
        position: fixed;
        left: 0;
        display: none;
        z-index: 2;
        .contents__menu {
          background-color: $gray3;
          & > ul {
            display: flex;
            padding: 20px 0 60px;
            & >li {
              width: 220px;
              h4 {
                padding: 3px 0 12px 0;
                font-size: 14px;
                color: $white;
              }
              ul li {
                padding: 5px 0;
                font-size: 12px;
                color: #999;
                cursor: pointer;
                &:hover {
                  color: $red;
                }
              }
            }
          }
        }
      }
      &:hover {
        .item__name {
          background-color: #2c2a29;
          color: $red;
          border-radius: 6px 6px 0 0;
        }
        .item__contents {
          display: block;
        }
      }
            

    }
  }
  .sns {
    position: absolute;
    bottom: 0;
    right: 0;
    display: flex;
    li{
      display: block;
      width: 20px;
      height: 20px;
      margin: 0 2px;
      margin-bottom: 34px;
      background-repeat: no-repeat;
      background-size: cover;
    }
    .facebook {
      background-image: url("../images/ic_sns_facebook.png");
     
    }
    .blog {
      background-image: url("../images/ic_sns_blog.png");

    }
    .instagram {
      background-image: url("../images/ic_sns_insta.png");
    }
    .youtube {
      background-image: url("../images/ic_sns_youtube.png");
    }
    .happypoint {
      background-image: url("../images/ic_sns_happy.png");
    }
  }

  .badges{
    position: fixed !important;
    bottom: 24px !important;
    display: block !important;
    overflow: initial !important;
    z-index: 10000000 !important;
    flex-wrap: nowrap !important;
    background: transparent !important;
    right: 24px !important;
    .textLauncherContext {
      will-change: transform, opacity, border-radius;
    display: block !important;
    position: absolute !important;
    height: 58px !important;
    top: 2px !important;
    padding: 20px 40px 20px 24px !important;
    background: rgb(255, 255, 255) !important;
    box-sizing: border-box !important;
    box-shadow: rgb(0 0 0 / 12%) 0px 3px 12px 0px !important;
    color: black !important;
    font-family: NotoSansKR, NotoSansJP, -apple-system, BlinkMacSystemFont, "Helvetica Neue", "맑은 고딕", "Yu Gothic", "Segoe UI", Roboto, system-ui, sans-serif !important;
    font-size: 14px !important;
    font-weight: 600 !important;
    line-height: 1.29 !important;
    text-overflow: ellipsis !important;
    overflow: hidden !important;
    white-space: nowrap !important;
    transition: box-shadow 0.2s ease-out 0s !important;
    user-select: none !important;
    cursor: pointer !important;
    right: 34px !important;
    border-radius: 30px 10px 10px 30px !important;
    text-align: right !important;
    animation: 0.5s cubic-bezier(0.24, 0.06, 0, 0.97) 0.55s 1 normal backwards running clICxo !important;
    }
    .badge {
      &::before{
        content: "" !important;
        display: block !important;
        flex-basis: initial !important;
        width: 34px !important;
        height: 34px !important;
        background-image: url(https://cdn.channel.io/plugin/images/ch-new-launcher-icon-68.png) !important;
        background-size: 136px !important;
        background-position: -34px 0px !important;
      }
      position: relative !important;
      overflow: initial !important;
      display: flex !important;
      -webkit-box-align: center !important;
      align-items: center !important;
      -webkit-box-pack: center !important;
      justify-content: center !important;
      box-shadow: rgb(0 0 0 / 15%) 0px 4px 6px 0px !important;
      transition: box-shadow 0.2s ease-out 0s !important;
      box-sizing: border-box !important;
      animation: 0.2s cubic-bezier(0.1, 0, 0.6, 1) 0.35s 1 normal backwards running fcEaNs !important;
      cursor: pointer !important;
      flex-wrap: nowrap !important;
      width: 62px !important;
      height: 62px !important;
      background: linear-gradient(rgb(184, 60, 60) 0%, rgb(184, 60, 60) 50%, rgb(184, 89, 60) 100%) !important;
      border-radius: 31px !important;
    }
    
  }
}

/* YOUTUBE */
.youtube {
  position: relative;
  height: 700px;
  background-color: $gray3;
  overflow: hidden;
}
.youtube .youtube__area {
  width: 1920px;
  position: absolute;
  left: 50%;
  margin-left: calc(1920px / -2);
  top: 50%;
  margin-top: calc(1920px * 9 / 16 / -2);
}
.youtube .youtube__area::before {
  content: "";
  display: block;
  width: 100%;
  height: 0;
  padding-top: 56.25%;
}
.youtube .youtube__cover {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
#player {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
}

.promotion {
  background-color: #f6f5ef;
  position: relative;
  transition: height .4s;
  overflow: hidden;
  border: 1px solid yellow;
  .inner{
    display: flex;
    .inner__left {
      width: 65%;
      height: 100%;
    }
    .inner__right {
      width: 35%;
      height: 100%;
      padding: 10px;
      border: 2px dotted blue;
    }
  }
  
  .swiper-container {
    width: 700px;
    height: 395px;
    border: 1px solid red;
    position: relative;
    left: 50%;
    margin-left: calc((700px) / -2);
  }
  .swiper-slide {
    opacity : .5;
    transition: 1s;
    img{ width: 700px;}
  }
  .swiper-slide-active {
    opacity: 1;
  }
  .swiper-pagination {
    bottom: 20px;
    .swiper-pagination-bullet {
      background-color: transparent;
      width: 12px;
      height: 12px;
      border-color: $gray3;
      border-radius: 50%;
      &:last-child {
        margin-right: 0;
      }
    }
    .swiper-pagination-bullet-active {
      border-color: red;
      border-radius: 50%;
    }

  }
  .swiper-prev, .swiper-next {
    width: 42px;
    height: 42px;
    border: 2px solid $white;
    background-color: $red;
    border-radius: 50%;
    position: absolute;
    bottom: 10px;
    z-index: 1;
    cursor: pointer;
    outline: none;
    display: flex;
    justify-content: center;
    align-items: center;
    color: $white;
  }
  .swiper-prev {
    left: 600px;
  }
  .swiper-next {
    left: 650px;
  }
}

// FOOTER
footer {
  background-color: #333;
  color: #999;
  font-size: 12px;
  .inner {
    padding: 40px 0 60px 0;
  }
  .menu {
    display: flex;
    justify-content: center;
    li {
      position: relative;
      &::before {
        content: "";
        width: 1px;
        height: 12px;
        background-color: #555;
        position: absolute;
        top: 0;
        bottom: 0;
        right: -1px;
        margin: auto;
      }
      &:last-child::before {
        display: none;
      }
      a {
        color: #ccc;
        font-weight: 700;
        padding: 15px;
        display: block;
      }
      a.red {
        color: $red;
      }
    }
  }
  .info {
    margin-top: 30px;
    text-align: center;
    span {
      margin-right: 20px;
      &:last-child {
        margin-right: 0;
      }
    }
  }
  .copyright {
    text-align: center;
    margin-top: 12px;
  }
  .logo {
    margin: 30px auto 0;
  }
}

#to-top {
  width: 42px;
  height: 42px;
  background-color: #333;
  color: #fff;
  border: 2px solid #fff;
  border-radius: 10px;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  bottom: 100px;
  right: 30px;
  z-index: 9;
}
